import React from 'react';

const EmailPreview = ({ data, onClose, onSend }) => (
  <div>
    <style>
      {`
        .app-container {
          font-family: 'Segoe UI', sans-serif;
          max-width: 650px;
          margin: 20px auto;
          padding: 25px;
          background: #f7fafc;
          border-radius: 15px;
          box-shadow: 0 6px 12px rgba(0, 0, 0, 0.05);
        }
        .header {
          text-align: center;
          padding: 20px;
          background: #4a90e2;
          border-radius: 15px 15px 0 0;
          margin: -25px -25px 25px -25px;
          color: white;
        }
        .header h1 {
          font-size: 30px;
          font-weight: 600;
          margin: 0;
        }
        .progress-bar {
          width: 100%;
          height: 10px;
          background: #e6eef7;
          border-radius: 5px;
          margin-top: 10px;
          overflow: hidden;
        }
        .progress {
          width: 100%; /* 100% as this is the final preview step */
          height: 100%;
          background: #50c878;
          transition: width 0.3s ease;
          border-radius: 5px;
        }
        .email-container {
          background: #ffffff;
          padding: 25px;
          border-radius: 8px;
          border: 1px solid #e6eef7;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.05);
        }
        .email-header {
          text-align: center;
          padding-bottom: 15px;
          border-bottom: 1px solid #e6eef7;
          margin-bottom: 20px;
        }
        .email-header h2 {
          font-size: 22px;
          font-weight: 600;
          color: #2c5282;
          margin: 0;
        }
        .email-body {
          margin-bottom: 20px;
        }
        .email-item {
          margin-bottom: 12px;
          font-size: 15px;
          color: #4a5568;
        }
        .email-item strong {
          color: #9b59b6;
        }
        .email-footer {
          text-align: center;
          padding-top: 15px;
          border-top: 1px solid #e6eef7;
          font-size: 13px;
          color: #718096;
        }
        .btn {
          padding: 14px 20px;
          border: none;
          border-radius: 6px;
          font-size: 16px;
          font-weight: 500;
          cursor: pointer;
          transition: background-color 0.3s ease, transform 0.2s ease;
        }
        .btn-send {
          background: #9b59b6;
          color: white;
        }
        .btn-send:hover {
          background: #8e44ad;
          transform: translateY(-2px);
        }
        .btn-cancel {
          background: #7f8c8d;
          color: white;
        }
        .btn-cancel:hover {
          background: #6c757d;
          transform: translateY(-2px);
        }
        .btn-group {
          margin-top: 20px;
          display: flex;
          gap: 10px;
        }
        @media (max-width: 600px) {
          .app-container { margin: 10px; padding: 15px; }
          .header { margin: -15px -15px 15px -15px; }
          .email-container { padding: 15px; }
          .email-header h2 { font-size: 18px; }
          .btn { padding: 12px; }
          .btn-group { flex-direction: column; gap: 8px; }
        }
      `}
    </style>
    <div className="app-container">
      <div className="header">
        <h1>NOC Email Sender</h1>
        <div className="progress-bar">
          <div className="progress"></div>
        </div>
      </div>
      <div className="email-container">
        <div className="email-header">
          <h2>Incident Notification</h2>
        </div>
        <div className="email-body">
          <p className="email-item"><strong>Recipient Group:</strong> {data.recipient_group || 'N/A'}</p>
          <p className="email-item"><strong>Severity:</strong> {data.severity || 'N/A'}</p>
          <p className="email-item"><strong>Trigger Name:</strong> {data.trigger_name || 'N/A'}</p>
          <p className="email-item"><strong>Event Date:</strong> {data.event_date || 'N/A'}</p>
          <p className="email-item"><strong>Event Start Time:</strong> {data.event_start_time || 'N/A'}</p>
          <p className="email-item"><strong>Description:</strong> {data.trigger_description || 'N/A'}</p>
          <p className="email-item"><strong>Host Name:</strong> {data.host_name || 'N/A'}</p>
          <p className="email-item"><strong>Host IP:</strong> {data.host_ip || 'N/A'}</p>
        </div>
        <div className="email-footer">
          <p>Sent via NOC Email Sender - {new Date().toLocaleString('en-US', { timeZone: 'Asia/Colombo', hour12: true })}</p>
          <p>Contact support at noc@groupit.hayleys.com for assistance.</p>
        </div>
      </div>
      <div className="btn-group">
        <button onClick={onSend} className="btn btn-send">Confirm Send</button>
        <button onClick={onClose} className="btn btn-cancel">Cancel</button>
      </div>
    </div>
  </div>
);

export default EmailPreview;
