import React from 'react';

const Step1 = ({ formData, handleChange, groups, nextStep }) => (
  <div style={{ marginTop: '20px' }}>
    <h2>Step 1: Incident Details</h2>
    {groups.length === 0 && <p style={{ color: '#dc3545' }}>No recipient groups available.</p>}
    <div style={{ marginBottom: '15px' }}>
      <label style={{ display: 'block', marginBottom: '5px' }}>Recipient Group:</label>
      <select
        name="recipient_group"
        value={formData.recipient_group}
        onChange={handleChange}
        style={{ width: '100%', padding: '8px', borderRadius: '4px' }}
        required
      >
        <option value="">Select</option>
        {groups.map(g => <option key={g} value={g}>{g}</option>)}
      </select>
    </div>
    <div style={{ marginBottom: '15px' }}>
      <label style={{ display: 'block', marginBottom: '5px' }}>Severity:</label>
      <select
        name="severity"
        value={formData.severity}
        onChange={handleChange}
        style={{ width: '100%', padding: '8px', borderRadius: '4px' }}
      >
        <option>High</option>
        <option>Medium</option>
        <option>Low</option>
      </select>
    </div>
    <div style={{ marginBottom: '15px' }}>
      <label style={{ display: 'block', marginBottom: '5px' }}>Trigger Name:</label>
      <input
        name="trigger_name"
        value={formData.trigger_name}
        onChange={handleChange}
        style={{ width: '100%', padding: '8px', borderRadius: '4px' }}
        required
      />
    </div>
    <button
      onClick={nextStep}
      style={{ padding: '10px 20px', background: '#007bff', color: 'white', borderRadius: '4px', border: 'none' }}
      disabled={!formData.recipient_group || !formData.trigger_name}
    >
      Next
    </button>
  </div>
);

export default Step1;
