import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EmailPreview = ({ data, onClose, onSend, isLoading }) => {
  console.log('Received Preview Data:', data);
  const [recipientDetails, setRecipientDetails] = useState({ to: '', cc: '' });
  const [loadingDetails, setLoadingDetails] = useState(false);

  useEffect(() => {
    if (data && data.recipient_group) {
      fetchRecipientDetails(data.recipient_group);
      setEditedData(data);
    }
  }, [data]);

  const handleEditChange = (field, value) => {
    setEditedData(prev => ({ ...prev, [field]: value }));
  };

  const saveChanges = () => {
    setIsEditing(false);
    // You could also call a callback to update the parent component
  };

  const fetchRecipientDetails = async (groupName) => {
    setLoadingDetails(true);
    try {
      const response = await axios.get(`/api/email/groups/${groupName}`);
      setRecipientDetails({
        to: response.data.to || '',
        cc: response.data.cc || ''
      });
    } catch (error) {
      console.error('Error fetching recipient details:', error);
      setRecipientDetails({ to: '', cc: '' });
      // You could also set an error state here if needed
    } finally {
      setLoadingDetails(false);
    }
  };

  const hasError = !isLoading && !loadingDetails && (!data || !data.recipient_group || !recipientDetails.to);

  const openEmailClient = () => {
    if (!editedData || !recipientDetails.to) {
      alert('Email details not available');
      return;
    }

    // Create email body content
    const emailBody = `
Incident Notification - ${editedData.trigger_name || 'N/A'}

INCIDENT DETAILS:
- Severity: ${editedData.severity || 'N/A'}
- Trigger Name: ${editedData.trigger_name || 'N/A'}
- Event Date: ${editedData.event_date || 'N/A'}
- Event Time: ${editedData.event_start_time || 'N/A'}
- Description: ${editedData.trigger_description || 'N/A'}

HOST INFORMATION:
- Host Name: ${editedData.host_name || 'N/A'}
- Host IP: ${editedData.host_ip || 'N/A'}

This is an automated alert from the Network Operations Center.
Please review and edit this email before sending.

---
Sent via NOC Email Sender - ${new Date().toLocaleString('en-US', { timeZone: 'Asia/Colombo', hour12: true })}
Contact support at noc@groupit.hayleys.com for assistance.
    `.trim();

    // Create mailto link
    const subject = encodeURIComponent(`[${editedData.severity || 'ALERT'}] ${editedData.trigger_name || 'Incident Report'}`);
    const body = encodeURIComponent(emailBody);
    const to = encodeURIComponent(recipientDetails.to);
    const cc = recipientDetails.cc ? encodeURIComponent(recipientDetails.cc) : '';
    
    let mailtoLink = `mailto:${to}?subject=${subject}&body=${body}`;
    if (cc) {
      mailtoLink += `&cc=${cc}`;
    }

    // Open email client
    window.open(mailtoLink);
  };

  return (
    <div>
      <style>
        {`
          .app-container {
            font-family: 'Segoe UI', sans-serif;
            max-width: 650px;
            margin: 20px auto;
            padding: 25px;
            background: #f7fafc;
            border-radius: 15px;
            box-shadow: 0 6px 12px rgba(0, 0, 0, 0.05);
          }
          .header {
            text-align: center;
            padding: 20px;
            background: #4a90e2;
            border-radius: 15px 15px 0 0;
            margin: -25px -25px 25px -25px;
            color: white;
          }
          .header h1 {
            font-size: 30px;
            font-weight: 600;
            margin: 0;
          }
          .progress-bar {
            width: 100%;
            height: 10px;
            background: #e6eef7;
            border-radius: 5px;
            margin-top: 10px;
            overflow: hidden;
          }
          .progress {
            width: 100%;
            height: 100%;
            background: #50c878;
            transition: width 0.3s ease;
            border-radius: 5px;
          }
          .email-container {
            background: #ffffff;
            padding: 25px;
            border-radius: 8px;
            border: 1px solid #e6eef7;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.05);
          }
          .email-header {
            text-align: center;
            padding-bottom: 15px;
            border-bottom: 1px solid #e6eef7;
            margin-bottom: 20px;
          }
          .email-header h2 {
            font-size: 22px;
            font-weight: 600;
            color: #2c5282;
            margin: 0;
          }
          .email-body {
            margin-bottom: 20px;
          }
          .email-item {
            margin-bottom: 12px;
            font-size: 15px;
            color: #4a5568;
          }
          .email-item strong {
            color: #9b59b6;
          }
          .email-footer {
            text-align: center;
            padding-top: 15px;
            border-top: 1px solid #e6eef7;
            font-size: 13px;
            color: #718096;
          }
          .btn {
            padding: 14px 20px;
            border: none;
            border-radius: 6px;
            font-size: 16px;
            font-weight: 500;
            cursor: pointer;
            transition: background-color 0.3s ease, transform 0.2s ease;
          }
          .btn-send {
            background: #9b59b6;
            color: white;
          }
          .btn-send:hover {
            background: #8e44ad;
            transform: translateY(-2px);
          }
          .btn-cancel {
            background: #7f8c8d;
            color: white;
          }
           .btn-cancel:hover {
             background: #6c757d;
             transform: translateY(-2px);
           }
           .btn-edit {
             background: #17a2b8;
             color: white;
           }
           .btn-edit:hover {
             background: #138496;
             transform: translateY(-2px);
           }
           .btn-group {
             margin-top: 20px;
             display: flex;
             gap: 10px;
           }
          .status-message {
            color: #e53e3e;
            font-size: 14px;
            text-align: center;
            margin-bottom: 15px;
          }
          .loading-message {
            color: #4a5568;
            font-size: 14px;
            text-align: center;
            margin-bottom: 15px;
          }
          @media (max-width: 600px) {
            .app-container { margin: 10px; padding: 15px; }
            .header { margin: -15px -15px 15px -15px; }
            .email-container { padding: 15px; }
            .email-header h2 { font-size: 18px; }
            .btn { padding: 12px; }
            .btn-group { flex-direction: column; gap: 8px; }
          }
        `}
      </style>
      <div className="app-container">
        <div className="header">
          <h1>NOC Email Sender</h1>
          <div className="progress-bar">
            <div className="progress"></div>
          </div>
        </div>
        {(isLoading || loadingDetails) && <p className="loading-message">Loading preview data...</p>}
        {hasError && (
          <p className="status-message">
            Error: Recipient group or email details not available. Please select a valid group or check the server connection.
          </p>
        )}
        {!isLoading && !loadingDetails && !hasError && (
          <div className="email-container">
            <div className="email-header">
              <h2>Incident Notification</h2>
            </div>
             <div className="email-body">
               <p className="email-item"><strong>Recipient Group:</strong> {editedData.recipient_group || 'N/A'}</p>
               <p className="email-item"><strong>To:</strong> {recipientDetails.to || 'N/A'}</p>
               <p className="email-item"><strong>CC:</strong> {recipientDetails.cc || 'N/A'}</p>
               <p className="email-item"><strong>Severity:</strong> {isEditing ? (
                 <select value={editedData.severity} onChange={(e) => handleEditChange('severity', e.target.value)} style={{padding: '2px 5px', border: '1px solid #ccc', borderRadius: '3px'}}>
                   <option value="High">High</option>
                   <option value="Medium">Medium</option>
                   <option value="Low">Low</option>
                 </select>
               ) : (editedData.severity || 'N/A')}</p>
               <p className="email-item"><strong>Trigger Name:</strong> {isEditing ? (
                 <input type="text" value={editedData.trigger_name} onChange={(e) => handleEditChange('trigger_name', e.target.value)} style={{padding: '2px 5px', border: '1px solid #ccc', borderRadius: '3px', width: '200px'}} />
               ) : (editedData.trigger_name || 'N/A')}</p>
               <p className="email-item"><strong>Event Date:</strong> {isEditing ? (
                 <input type="date" value={editedData.event_date} onChange={(e) => handleEditChange('event_date', e.target.value)} style={{padding: '2px 5px', border: '1px solid #ccc', borderRadius: '3px'}} />
               ) : (editedData.event_date || 'N/A')}</p>
               <p className="email-item"><strong>Event Start Time:</strong> {isEditing ? (
                 <input type="time" value={editedData.event_start_time} onChange={(e) => handleEditChange('event_start_time', e.target.value)} style={{padding: '2px 5px', border: '1px solid #ccc', borderRadius: '3px'}} />
               ) : (editedData.event_start_time || 'N/A')}</p>
               <p className="email-item"><strong>Description:</strong> {isEditing ? (
                 <textarea value={editedData.trigger_description} onChange={(e) => handleEditChange('trigger_description', e.target.value)} style={{padding: '5px', border: '1px solid #ccc', borderRadius: '3px', width: '100%', minHeight: '60px', resize: 'vertical'}} />
               ) : (editedData.trigger_description || 'N/A')}</p>
               <p className="email-item"><strong>Host Name:</strong> {isEditing ? (
                 <input type="text" value={editedData.host_name} onChange={(e) => handleEditChange('host_name', e.target.value)} style={{padding: '2px 5px', border: '1px solid #ccc', borderRadius: '3px', width: '200px'}} />
               ) : (editedData.host_name || 'N/A')}</p>
               <p className="email-item"><strong>Host IP:</strong> {isEditing ? (
                 <input type="text" value={editedData.host_ip} onChange={(e) => handleEditChange('host_ip', e.target.value)} style={{padding: '2px 5px', border: '1px solid #ccc', borderRadius: '3px', width: '200px'}} />
               ) : (editedData.host_ip || 'N/A')}</p>
             </div>
            <div className="email-footer">
              <p>Sent via NOC Email Sender - {new Date().toLocaleString('en-US', { timeZone: 'Asia/Colombo', hour12: true })}</p>
              <p>Contact support at noc@groupit.hayleys.com for assistance.</p>
            </div>
          </div>
        )}
         <div className="btn-group">
           <button onClick={openEmailClient} className="btn btn-edit" disabled={isLoading || loadingDetails || hasError}>Open in Email Client</button>
           <button onClick={onSend} className="btn btn-send" disabled={isLoading || loadingDetails || hasError}>Confirm Send</button>
           <button onClick={onClose} className="btn btn-cancel" disabled={isLoading || loadingDetails}>Cancel</button>
         </div>
      </div>
    </div>
  );
};

export default EmailPreview;
