import React from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const Step2 = ({ formData, handleChange, nextStep, prevStep }) => {
  const handleDateChange = (date) => {
    handleChange({ target: { name: 'event_date', value: date.toISOString().split('T')[0] } });
  };

  return (
    <div style={{ marginTop: '20px' }}>
      <h2>Step 2: Event Details</h2>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Event Date:</label>
        <DatePicker
          selected={new Date(formData.event_date)}
          onChange={handleDateChange}
          dateFormat="yyyy-MM-dd"
          style={{ width: '100%', padding: '8px', borderRadius: '4px' }}
          required
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Event Start Time:</label>
        <input
          type="time"
          name="event_start_time"
          value={formData.event_start_time}
          onChange={handleChange}
          style={{ width: '100%', padding: '8px', borderRadius: '4px' }}
          required
        />
      </div>
      <button
        onClick={prevStep}
        style={{ padding: '10px 20px', background: '#6c757d', color: 'white', borderRadius: '4px', border: 'none', marginRight: '10px' }}
      >
        Previous
      </button>
      <button
        onClick={nextStep}
        style={{ padding: '10px 20px', background: '#007bff', color: 'white', borderRadius: '4px', border: 'none' }}
      >
        Next
      </button>
    </div>
  );
};

export default Step2;
