import React from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const Step2 = ({ formData, handleChange, nextStep, prevStep }) => {
  const handleDateChange = (date) => {
    handleChange({ target: { name: 'event_date', value: date.toISOString().split('T')[0] } });
  };

  return (
    <div>
      <style>
        {`
          .app-container {
            font-family: 'Segoe UI', sans-serif;
            max-width: 600px;
            margin: 20px auto;
            padding: 25px;
            background: #f7fafc;
            border-radius: 15px;
            box-shadow: 0 6px 12px rgba(0, 0, 0, 0.05);
          }
          .header {
            text-align: center;
            padding: 20px;
            background: #4a90e2;
            border-radius: 15px 15px 0 0;
            margin: -25px -25px 25px -25px;
            color: white;
          }
          .header h1 {
            font-size: 30px;
            font-weight: 600;
            margin: 0;
          }
          .progress-bar {
            width: 100%;
            height: 10px;
            background: #e6eef7;
            border-radius: 5px;
            margin-top: 10px;
            overflow: hidden;
          }
          .progress {
            width: 66.66%; /* 66.66% for Step 2 */
            height: 100%;
            background: #50c878;
            transition: width 0.3s ease;
            border-radius: 5px;
          }
          .step-title {
            font-size: 24px;
            font-weight: 500;
            color: #2c5282;
            text-align: center;
            margin-bottom: 20px;
          }
          .form-group {
            margin-bottom: 20px;
            padding: 15px;
            background: #ffffff;
            border-radius: 8px;
            border: 1px solid #e6eef7;
            transition: box-shadow 0.3s ease;
          }
          .form-group:hover {
            box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
          }
          .form-label {
            display: block;
            font-size: 15px;
            font-weight: 500;
            color: #4a5568;
            margin-bottom: 8px;
          }
          select, input, .react-datepicker__input-container input {
            width: 100%;
            padding: 12px;
            border: 1px solid #e6eef7;
            border-radius: 6px;
            font-size: 15px;
            background: #f7fafc;
            transition: border-color 0.3s ease;
          }
          select:focus, input:focus, .react-datepicker__input-container input:focus {
            border-color: #9b59b6;
            outline: none;
          }
          .btn {
            padding: 14px 20px;
            border: none;
            border-radius: 6px;
            font-size: 16px;
            font-weight: 500;
            cursor: pointer;
            transition: background-color 0.3s ease, transform 0.2s ease;
          }
          .btn-prev {
            background: #7f8c8d;
            color: white;
          }
          .btn-prev:hover {
            background: #6c757d;
            transform: translateY(-2px);
          }
          .btn-next {
            background: #9b59b6;
            color: white;
          }
          .btn-next:hover {
            background: #8e44ad;
            transform: translateY(-2px);
          }
          .btn-group {
            display: flex;
            gap: 10px;
          }
          @media (max-width: 600px) {
            .app-container { margin: 10px; padding: 15px; }
            .header { margin: -15px -15px 15px -15px; }
            .step-title { font-size: 20px; }
            .form-group { padding: 10px; }
            .btn { padding: 12px; }
            .btn-group { flex-direction: column; gap: 8px; }
          }
        `}
      </style>
      <div className="app-container">
        <div className="header">
          <h1>NOC Email Sender</h1>
          <div className="progress-bar">
            <div className="progress"></div>
          </div>
        </div>
        <h2 className="step-title">Step 2: Event Details</h2>
        <div className="form-group">
          <label className="form-label">Event Date</label>
          <DatePicker
            selected={new Date(formData.event_date)}
            onChange={handleDateChange}
            dateFormat="yyyy-MM-dd"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Event Start Time</label>
          <input
            type="time"
            name="event_start_time"
            value={formData.event_start_time}
            onChange={handleChange}
          />
        </div>
        <div className="btn-group">
          <button onClick={prevStep} className="btn btn-prev">Previous</button>
          <button onClick={nextStep} className="btn btn-next">Next</button>
        </div>
      </div>
    </div>
  );
};

export default Step2;
