import React from 'react';

const Step3 = ({ formData, handleChange, prevStep, handlePreview }) => (
  <div style={{ marginTop: '20px' }}>
    <h2>Step 3: Additional Details</h2>
    <div style={{ marginBottom: '15px' }}>
      <label style={{ display: 'block', marginBottom: '5px' }}>Trigger Description:</label>
      <textarea
        name="trigger_description"
        value={formData.trigger_description}
        onChange={handleChange}
        style={{ width: '100%', padding: '8px', borderRadius: '4px', minHeight: '100px' }}
      />
    </div>
    <div style={{ marginBottom: '15px' }}>
      <label style={{ display: 'block', marginBottom: '5px' }}>Host Name:</label>
      <input
        name="host_name"
        value={formData.host_name}
        onChange={handleChange}
        style={{ width: '100%', padding: '8px', borderRadius: '4px' }}
        required
      />
    </div>
    <div style={{ marginBottom: '15px' }}>
      <label style={{ display: 'block', marginBottom: '5px' }}>Host IP:</label>
      <input
        name="host_ip"
        value={formData.host_ip}
        onChange={handleChange}
        style={{ width: '100%', padding: '8px', borderRadius: '4px' }}
        required
      />
    </div>
    <button
      onClick={prevStep}
      style={{ padding: '10px 20px', background: '#6c757d', color: 'white', borderRadius: '4px', border: 'none', marginRight: '10px' }}
    >
      Previous
    </button>
    <button
      onClick={handlePreview}
      style={{ padding: '10px 20px', background: '#007bff', color: 'white', borderRadius: '4px', border: 'none' }}
      disabled={!formData.trigger_name || !formData.host_name || !formData.host_ip}
    >
      Preview & Send
    </button>
  </div>
);

export default Step3;
