import React, { useState, useEffect } from 'react';
import Step1 from './components/Step1';
import Step2 from './components/Step2';
import Step3 from './components/Step3';
import EmailPreview from './components/EmailPreview';
import axios from 'axios';

const MultiStepForm = ({ token, setToken }) => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    recipient_group: '',
    severity: 'High',
    trigger_name: '',
    event_date: new Date().toISOString().split('T')[0],
    event_start_time: '00:00',
    trigger_description: '',
    host_name: '',
    host_ip: ''
  });
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showPreview, setShowPreview] = useState(false);
  const [previewData, setPreviewData] = useState({});
  const [error, setError] = useState('');

  // Fetch groups on mount
  useEffect(() => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchGroups();
    }
  }, [token]);

  const fetchGroups = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/email/groups');
      setGroups(res.data);
    } catch (err) {
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        setToken(null);
        setError('Session expired. Please log in again.');
      } else {
        setError('Failed to load groups. Check backend.');
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchGroupEmails = async (groupName) => {
    try {
      const res = await axios.get(`/api/email/groups?name=${encodeURIComponent(groupName)}`);
      const group = res.data[0] || {};
      return { to: group.to || 'N/A', cc: group.cc || 'N/A' };
    } catch {
      return { to: 'N/A', cc: 'N/A' };
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const nextStep = () => {
    if (step === 1 && !formData.recipient_group) {
      setError('Please select a recipient group.');
      return;
    }
    setError('');
    setStep(step + 1);
  };

  const prevStep = () => {
    setError('');
    setStep(step - 1);
  };

  const handlePreview = async () => {
    if (!formData.trigger_name || !formData.host_name || !formData.host_ip) {
      setError('Trigger name, host name, and host IP are required.');
      return;
    }
    const emails = await fetchGroupEmails(formData.recipient_group);
    setPreviewData({ ...formData, ...emails });
    setShowPreview(true);
  };

  const handleSend = async () => {
    try {
      await axios.post('/api/email/send', previewData);
      alert('Email sent!');
      setShowPreview(false);
      setFormData({
        recipient_group: '',
        severity: 'High',
        trigger_name: '',
        event_date: new Date().toISOString().split('T')[0],
        event_start_time: '00:00',
        trigger_description: '',
        host_name: '',
        host_ip: ''
      });
      setStep(1);
    } catch (err) {
      setError(err.response?.data?.error || 'Error sending email');
    }
  };

  if (loading) return <p>Loading groups...</p>;

  return (
    <div>
      {error && <p style={{ color: '#dc3545', textAlign: 'center' }}>{error}</p>}
      <progress value={step} max="3" style={{ width: '100%' }} />
      {step === 1 && <Step1 formData={formData} handleChange={handleChange} groups={groups} nextStep={nextStep} />}
      {step === 2 && <Step2 formData={formData} handleChange={handleChange} nextStep={nextStep} prevStep={prevStep} />}
      {step === 3 && <Step3 formData={formData} handleChange={handleChange} prevStep={prevStep} handlePreview={handlePreview} />}
      {showPreview && <EmailPreview data={previewData} onClose={() => setShowPreview(false)} onSend={handleSend} />}
    </div>
  );
};

export default MultiStepForm;
