import React from 'react';

const Step1 = ({ formData, handleChange, groups, nextStep }) => {
  return (
    <div>
      <h2>Step 1: Select Recipient Group</h2>
      <select name="recipient_group" value={formData.recipient_group || ''} onChange={handleChange}>
        <option value="">Select a group</option>
        {groups.map((group, index) => (
          <option key={index} value={group.name}>{group.name}</option>
        ))}
      </select>
      <button onClick={nextStep} disabled={!formData.recipient_group}>Next</button>
    </div>
  );
};

export default Step1;
