import React from 'react';

const Step1 = ({ formData, handleChange, groups, nextStep }) => (
  <div>
    <style>
      {`
        .app-container {
          font-family: 'Verdana', sans-serif;
          max-width: 550px;
          margin: 20px auto;
          padding: 20px;
          background: #ffffff;
          border-radius: 12px;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        }
        .header {
          text-align: center;
          padding: 15px;
          background: #f0f4f8;
          border-radius: 12px 12px 0 0;
          margin: -20px -20px 20px -20px;
        }
        .header h1 {
          font-size: 28px;
          font-weight: 600;
          color: #2c3e50;
          margin: 0;
        }
        .progress-bar {
          width: 100%;
          height: 8px;
          background: #e0e7f0;
          border-radius: 4px;
          margin-top: 10px;
        }
        .progress {
          width: 33.33%;
          height: 100%;
          background: #3498db;
          border-radius: 4px;
        }
        .step-title {
          font-size: 24px;
          font-weight: 500;
          color: #34495e;
          text-align: center;
          margin-bottom: 25px;
        }
        .form-group {
          margin-bottom: 20px;
        }
        .form-label {
          display: block;
          font-size: 15px;
          font-weight: 500;
          color: #34495e;
          margin-bottom: 8px;
        }
        select, input {
          width: 100%;
          padding: 10px;
          border: 1px solid #d3dbe3;
          border-radius: 6px;
          font-size: 15px;
          background: #f9fbfc;
          transition: border-color 0.2s ease;
        }
        select:focus, input:focus {
          border-color: #3498db;
          outline: none;
        }
        .btn {
          width: 100%;
          padding: 12px;
          border: none;
          border-radius: 6px;
          font-size: 16px;
          font-weight: 500;
          color: white;
          background: #3498db;
          cursor: pointer;
          transition: background-color 0.2s ease;
        }
        .btn:hover {
          background: #2980b9;
        }
        .error-message {
          color: #e74c3c;
          font-size: 14px;
          margin-top: 5px;
          text-align: center;
        }
        @media (max-width: 600px) {
          .app-container { margin: 10px; padding: 15px; }
          .header { margin: -15px -15px 15px -15px; }
          .step-title { font-size: 20px; }
          .btn { padding: 10px; }
        }
      `}
    </style>
    <div className="app-container">
      <div className="header">
        <h1>NOC Email Sender</h1>
        <div className="progress-bar">
          <div className="progress"></div>
        </div>
      </div>
      <h2 className="step-title">Step 1: Incident Details</h2>
      {groups.length === 0 && <p className="error-message">No recipient groups available.</p>}
      <div className="form-group">
        <label className="form-label">Recipient Group</label>
        <select
          name="recipient_group"
          value={formData.recipient_group}
          onChange={handleChange}
        >
          <option value="">Select</option>
          {groups.map(g => <option key={g} value={g}>{g}</option>)}
        </select>
      </div>
      <div className="form-group">
        <label className="form-label">Severity</label>
        <select
          name="severity"
          value={formData.severity}
          onChange={handleChange}
        >
          <option value="">Select</option>
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
      </div>
      <div className="form-group">
        <label className="form-label">Trigger Name</label>
        <input
          name="trigger_name"
          value={formData.trigger_name}
          onChange={handleChange}
        />
      </div>
      <button
        onClick={nextStep}
        className="btn"
      >
        Next
      </button>
    </div>
  </div>
);

export default Step1;
