import React from 'react';

const EmailPreview = ({ data, onClose, onSend }) => (
  <div>
    <style>
      {`
        .preview-container {
          position: fixed;
          top: 25%;
          left: 15%;
          right: 15%;
          padding: 24px;
          background: linear-gradient(135deg, #f0f4f8, #ffffff);
          border-radius: 12px;
          box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
          transform: translateY(-10px);
          transition: transform 0.3s ease;
        }
        .preview-container:hover {
          transform: translateY(0);
        }
        .preview-title {
          font-size: 28px;
          font-weight: bold;
          color: #2c3e50;
          background: linear-gradient(90deg, #2ecc71, #e67e22);
          -webkit-background-clip: text;
          background-clip: text;
          color: transparent;
          margin-bottom: 24px;
        }
        .preview-item {
          margin-bottom: 12px;
          font-size: 16px;
          color: #34495e;
        }
        .preview-item strong {
          color: #2ecc71;
        }
        .btn {
          padding: 12px 24px;
          border: none;
          border-radius: 8px;
          font-size: 16px;
          cursor: pointer;
          transition: transform 0.3s ease, background 0.3s ease;
        }
        .btn-send {
          background: linear-gradient(90deg, #27ae60, #2ecc71);
          color: white;
        }
        .btn-send:hover {
          transform: scale(1.05);
          background: linear-gradient(90deg, #219653, #27ae60);
        }
        .btn-cancel {
          background: linear-gradient(90deg, #e74c3c, #c0392b);
          color: white;
        }
        .btn-cancel:hover {
          transform: scale(1.05);
          background: linear-gradient(90deg, #d35400, #e74c3c);
        }
        .btn-group {
          margin-top: 24px;
          display: flex;
          gap: 16px;
        }
        @media (max-width: 600px) {
          .preview-container {
            top: 10%;
            left: 5%;
            right: 5%;
            padding: 16px;
          }
          .preview-title { font-size: 24px; }
          .btn { padding: 10px 20px; }
          .btn-group { flex-direction: column; gap: 10px; }
        }
      `}
    </style>
    <div className="preview-container">
      <h2 className="preview-title">Email Preview</h2>
      <div>
        <p className="preview-item"><strong>Recipient Group:</strong> {data.recipient_group}</p>
        <p className="preview-item"><strong>Severity:</strong> {data.severity}</p>
        <p className="preview-item"><strong>Trigger Name:</strong> {data.trigger_name}</p>
        <p className="preview-item"><strong>Event Date:</strong> {data.event_date}</p>
        <p className="preview-item"><strong>Event Start Time:</strong> {data.event_start_time}</p>
        <p className="preview-item"><strong>Description:</strong> {data.trigger_description}</p>
        <p className="preview-item"><strong>Host Name:</strong> {data.host_name}</p>
        <p className="preview-item"><strong>Host IP:</strong> {data.host_ip}</p>
      </div>
      <div className="btn-group">
        <button onClick={onSend} className="btn btn-send">Confirm Send</button>
        <button onClick={onClose} className="btn btn-cancel">Cancel</button>
      </div>
    </div>
  </div>
);

export default EmailPreview;
