import React from 'react';

const EmailPreview = ({ data, onClose, onSend }) => (
  <div style={{
    position: 'fixed', top: '20%', left: '20%', right: '20%', background: 'white',
    padding: '20px', border: '1px solid #ccc', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
  }}>
    <h2>Email Preview</h2>
    <p><b>Recipient Group:</b> {data.recipient_group}</p>
    <p><b>Severity:</b> {data.severity}</p>
    <p><b>Trigger Name:</b> {data.trigger_name}</p>
    <p><b>Event Date:</b> {data.event_date}</p>
    <p><b>Event Start Time:</b> {data.event_start_time}</p>
    <p><b>Description:</b> {data.trigger_description}</p>
    <p><b>Host Name:</b> {data.host_name}</p>
    <p><b>Host IP:</b> {data.host_ip}</p>
    <div style={{ marginTop: '20px' }}>
      <button
        onClick={onSend}
        style={{ padding: '10px 20px', background: '#28a745', color: 'white', borderRadius: '4px', border: 'none', marginRight: '10px' }}
      >
        Confirm Send
      </button>
      <button
        onClick={onClose}
        style={{ padding: '10px 20px', background: '#dc3545', color: 'white', borderRadius: '4px', border: 'none' }}
      >
        Cancel
      </button>
    </div>
  </div>
);

export default EmailPreview;
