import React, { useState, useEffect } from 'react';
import Step1 from './components/Step1';
import Step2 from './components/Step2';
import Step3 from './components/Step3';
import EmailPreview from './components/EmailPreview';

const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({});
  const [showPreview, setShowPreview] = useState(false);
  const [previewData, setPreviewData] = useState({});
  const [groups, setGroups] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    fetch('http://localhost:5000/api/email/groups')
      .then(response => response.json())
      .then(data => setGroups(data.map(group => group.name)))
      .catch(error => console.error('Error fetching groups:', error))
      .finally(() => setIsLoading(false));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const fetchGroupEmails = async (groupName) => {
    setIsLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/email/groups?name=${encodeURIComponent(groupName || '')}`);
      if (!response.ok) throw new Error('Server error');
      const groups = await response.json();
      return groups.length > 0 ? { to: groups[0].to, cc: groups[0].cc } : { to: 'N/A', cc: 'N/A' };
    } catch (error) {
      console.error('Fetch error:', error);
      return { to: 'N/A', cc: 'N/A' };
    } finally {
      setIsLoading(false);
    }
  };

  const handlePreview = async () => {
    const emails = await fetchGroupEmails(formData.recipient_group);
    setPreviewData({ ...formData, ...emails });
    setShowPreview(true);
  };

  const handleSend = () => {
    console.log('Sending email with data:', previewData);
    setShowPreview(false);
  };

  const handleClose = () => {
    setShowPreview(false);
  };

  const nextStep = () => setStep(prev => prev + 1);
  const prevStep = () => setStep(prev => prev - 1);

  if (showPreview) {
    return <EmailPreview data={previewData} onClose={handleClose} onSend={handleSend} isLoading={isLoading} />;
  }

  return (
    <div>
      {isLoading && <p>Loading...</p>}
      {!isLoading && (
        <>
          {step === 1 && <Step1 formData={formData} handleChange={handleChange} groups={groups} nextStep={nextStep} />}
          {step === 2 && <Step2 formData={formData} handleChange={handleChange} nextStep={nextStep} prevStep={prevStep} />}
          {step === 3 && <Step3 formData={formData} handleChange={handleChange} prevStep={prevStep} handlePreview={handlePreview} />}
        </>
      )}
    </div>
  );
};

export default MultiStepForm;
