import React from 'react';

const Step3 = ({ formData, handleChange, prevStep, handlePreview }) => {
  return (
    <div>
      <h2>Step 3: Additional Details</h2>
      <select name="severity" value={formData.severity || ''} onChange={handleChange}>
        <option value="">Select severity</option>
        <option value="Low">Low</option>
        <option value="Medium">Medium</option>
        <option value="High">High</option>
      </select>
      <input type="text" name="trigger_name" value={formData.trigger_name || ''} onChange={handleChange} placeholder="Trigger Name" />
      <input type="text" name="trigger_description" value={formData.trigger_description || ''} onChange={handleChange} placeholder="Description" />
      <input type="text" name="host_name" value={formData.host_name || ''} onChange={handleChange} placeholder="Host Name" />
      <input type="text" name="host_ip" value={formData.host_ip || ''} onChange={handleChange} placeholder="Host IP" />
      <button onClick={prevStep}>Previous</button>
      <button onClick={handlePreview} disabled={!formData.severity || !formData.trigger_name || !formData.trigger_description || !formData.host_name || !formData.host_ip}>Preview & Send</button>
    </div>
  );
};

export default Step3;
