// Step1.js
import React from 'react';

const Step1 = ({ formData, handleChange, groups, nextStep, setFormData }) => {
  const handleGroupChange = (e) => {
    const selectedGroup = groups.find(g => g.name === e.target.value);
    if (selectedGroup) {
      setFormData({
        ...formData,
        recipient_group: selectedGroup.name,
        to: selectedGroup.to,
        cc: selectedGroup.cc
      });
    }
  };

  return (
    <div>
      <h2>Step 1: Select Recipient Group</h2>
      <select value={formData.recipient_group} onChange={handleGroupChange}>
        <option value="">-- Select Group --</option>
        {groups.map((group) => (
          <option key={group._id || group.name} value={group.name}>
            {group.name}
          </option>
        ))}
      </select>
      <button onClick={nextStep}>Next</button>
    </div>
  );
};

export default Step1;
