import React from 'react';

const Step3 = ({ formData, handleChange, prevStep, handlePreview }) => (
  <div>
    <style>
      {`
        .step-container {
          margin-top: 24px;
          padding: 24px;
          background: linear-gradient(135deg, #f0f4f8, #ffffff);
          border-radius: 12px;
          box-shadow: 0 6px 12px rgba(0, 0, 0, 0.1);
        }
        .step-title {
          font-size: 28px;
          font-weight: bold;
          color: #2c3e50;
          background: linear-gradient(90deg, #2ecc71, #e67e22);
          -webkit-background-clip: text;
          background-clip: text;
          color: transparent;
          margin-bottom: 24px;
        }
        .form-group {
          margin-bottom: 20px;
        }
        .form-label {
          display: block;
          font-size: 16px;
          font-weight: 600;
          color: #34495e;
          margin-bottom: 8px;
        }
        textarea, input {
          width: 100%;
          padding: 12px;
          border: 2px solid #ecf0f1;
          border-radius: 8px;
          font-size: 14px;
          transition: border-color 0.3s ease, box-shadow 0.3s ease;
        }
        textarea:focus, input:focus {
          border-color: #2ecc71;
          box-shadow: 0 0 8px rgba(46, 204, 113, 0.3);
          outline: none;
        }
        .btn {
          padding: 12px 24px;
          border: none;
          border-radius: 8px;
          font-size: 16px;
          cursor: pointer;
          transition: transform 0.3s ease, background 0.3s ease;
        }
        .btn-prev {
          background: #7f8c8d;
          color: white;
        }
        .btn-prev:hover {
          transform: scale(1.05);
          background: #6c757d;
        }
        .btn-preview {
          background: linear-gradient(90deg, #2ecc71, #e67e22);
          color: white;
        }
        .btn-preview:hover {
          transform: scale(1.05);
          background: linear-gradient(90deg, #27ae60, #d35400);
        }
        .btn-preview:disabled {
          background: #bdc3c7;
          cursor: not-allowed;
        }
        .btn-group {
          display: flex;
          gap: 16px;
        }
        @media (max-width: 600px) {
          .step-container { padding: 16px; }
          .step-title { font-size: 24px; }
          .btn { padding: 10px 20px; }
          .btn-group { flex-direction: column; gap: 10px; }
        }
      `}
    </style>
    <div className="step-container">
      <h2 className="step-title">Step 3: Additional Details</h2>
      <div className="form-group">
        <label className="form-label">Trigger Description:</label>
        <textarea
          name="trigger_description"
          value={formData.trigger_description}
          onChange={handleChange}
          style={{ minHeight: '120px' }}
        />
      </div>
      <div className="form-group">
        <label className="form-label">Host Name:</label>
        <input
          name="host_name"
          value={formData.host_name}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label className="form-label">Host IP:</label>
        <input
          name="host_ip"
          value={formData.host_ip}
          onChange={handleChange}
          required
        />
      </div>
      <div className="btn-group">
        <button onClick={prevStep} className="btn btn-prev">Previous</button>
        <button
          onClick={handlePreview}
          disabled={!formData.trigger_name || !formData.host_name || !formData.host_ip}
          className="btn btn-preview"
        >
          Preview & Send
        </button>
      </div>
    </div>
  </div>
);

export default Step3;
