import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EmailPreview = ({ data, onClose, onSend, isLoading }) => {
  console.log('Received Preview Data:', data);
  const [recipientDetails, setRecipientDetails] = useState({ to: '', cc: '' });
  const [loadingDetails, setLoadingDetails] = useState(false);

  useEffect(() => {
    if (data && data.recipient_group) {
      fetchRecipientDetails(data.recipient_group);
    }
  }, [data]);

  const fetchRecipientDetails = async (groupName) => {
    setLoadingDetails(true);
    try {
      const response = await axios.get(`/api/email/groups/${groupName}`);
      setRecipientDetails({
        to: response.data.to || '',
        cc: response.data.cc || ''
      });
    } catch (error) {
      console.error('Error fetching recipient details:', error);
      setRecipientDetails({ to: '', cc: '' });
      // You could also set an error state here if needed
    } finally {
      setLoadingDetails(false);
    }
  };

  const hasError = !isLoading && !loadingDetails && (!data || !data.recipient_group || !recipientDetails.to);

  return (
    <div className="p-8">
      <div className="text-center mb-8">
        <div className="mx-auto h-16 w-16 bg-indigo-100 rounded-full flex items-center justify-center mb-4">
          <svg className="h-8 w-8 text-indigo-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
          </svg>
        </div>
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Email Preview</h2>
        <p className="text-gray-600">Review your email before sending</p>
      </div>

      {(isLoading || loadingDetails) && (
        <div className="text-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading preview data...</p>
        </div>
      )}

      {hasError && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-6 mb-6">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">Error</h3>
              <p className="text-sm text-red-700 mt-1">
                Recipient group or email details not available. Please select a valid group or check the server connection.
              </p>
            </div>
          </div>
        </div>
      )}

      {!isLoading && !loadingDetails && !hasError && (
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
          <div className="bg-gradient-to-r from-indigo-600 to-purple-600 px-6 py-4">
            <h3 className="text-lg font-semibold text-white">Incident Notification</h3>
            <p className="text-indigo-100 text-sm">Network Operations Center Alert</p>
          </div>
          
          <div className="p-6 space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="bg-gray-50 rounded-lg p-4">
                <h4 className="text-sm font-medium text-gray-500 mb-2">Recipient Information</h4>
                <div className="space-y-2 text-sm">
                  <div>
                    <span className="font-medium text-gray-700">Group:</span>
                    <span className="ml-2 text-gray-900">{data.recipient_group || 'N/A'}</span>
                  </div>
                  <div>
                    <span className="font-medium text-gray-700">To:</span>
                    <span className="ml-2 text-gray-900 break-all">{recipientDetails.to || 'N/A'}</span>
                  </div>
                  <div>
                    <span className="font-medium text-gray-700">CC:</span>
                    <span className="ml-2 text-gray-900 break-all">{recipientDetails.cc || 'N/A'}</span>
                  </div>
                </div>
              </div>

              <div className="bg-gray-50 rounded-lg p-4">
                <h4 className="text-sm font-medium text-gray-500 mb-2">Incident Details</h4>
                <div className="space-y-2 text-sm">
                  <div>
                    <span className="font-medium text-gray-700">Severity:</span>
                    <span className={`ml-2 px-2 py-1 rounded-full text-xs font-medium ${
                      data.severity === 'High' ? 'bg-red-100 text-red-800' :
                      data.severity === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-green-100 text-green-800'
                    }`}>
                      {data.severity || 'N/A'}
                    </span>
                  </div>
                  <div>
                    <span className="font-medium text-gray-700">Trigger:</span>
                    <span className="ml-2 text-gray-900">{data.trigger_name || 'N/A'}</span>
                  </div>
                  <div>
                    <span className="font-medium text-gray-700">Date:</span>
                    <span className="ml-2 text-gray-900">{data.event_date || 'N/A'}</span>
                  </div>
                  <div>
                    <span className="font-medium text-gray-700">Time:</span>
                    <span className="ml-2 text-gray-900">{data.event_start_time || 'N/A'}</span>
                  </div>
                </div>
              </div>
            </div>

            {data.trigger_description && (
              <div className="bg-gray-50 rounded-lg p-4">
                <h4 className="text-sm font-medium text-gray-500 mb-2">Description</h4>
                <p className="text-sm text-gray-900 whitespace-pre-wrap">{data.trigger_description}</p>
              </div>
            )}

            <div className="bg-gray-50 rounded-lg p-4">
              <h4 className="text-sm font-medium text-gray-500 mb-2">Host Information</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="font-medium text-gray-700">Host Name:</span>
                  <span className="ml-2 text-gray-900">{data.host_name || 'N/A'}</span>
                </div>
                <div>
                  <span className="font-medium text-gray-700">Host IP:</span>
                  <span className="ml-2 text-gray-900 font-mono">{data.host_ip || 'N/A'}</span>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-gray-50 px-6 py-4 border-t border-gray-200">
            <div className="text-center text-xs text-gray-500">
              <p>Sent via NOC Email Sender - {new Date().toLocaleString('en-US', { timeZone: 'Asia/Colombo', hour12: true })}</p>
              <p className="mt-1">Contact support at noc@groupit.hayleys.com for assistance.</p>
            </div>
          </div>
        </div>
      )}

      <div className="mt-8 flex justify-center space-x-4">
        <button
          onClick={onClose}
          disabled={isLoading || loadingDetails}
          className="inline-flex items-center px-6 py-3 border border-gray-300 text-base font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <svg className="mr-2 -ml-1 w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
          Cancel
        </button>
        
        <button
          onClick={onSend}
          disabled={isLoading || loadingDetails || hasError}
          className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-lg text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <svg className="mr-2 -ml-1 w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
            <path d="M2.003 5.884L10 9.882l7.997-3.998A2 2 0 0016 4H4a2 2 0 00-1.997 1.884z" />
            <path d="M18 8.118l-8 4-8-4V14a2 2 0 002 2h12a2 2 0 002-2V8.118z" />
          </svg>
          Confirm Send
        </button>
      </div>
    </div>
  );
};

export default EmailPreview;
