import React from 'react';

const Step2 = ({ formData, handleChange, nextStep, prevStep }) => {
  return (
    <div>
      <h2>Step 2: Event Details</h2>
      <input type="date" name="event_date" value={formData.event_date || ''} onChange={handleChange} />
      <input type="time" name="event_start_time" value={formData.event_start_time || ''} onChange={handleChange} />
      <button onClick={prevStep}>Previous</button>
      <button onClick={nextStep} disabled={!formData.event_date || !formData.event_start_time}>Next</button>
    </div>
  );
};

export default Step2;
